let findGroupedLightService = group => {

    for (let index = 0; index < group.services.length; ++index) {

        let service = group.services[index]

        if (service.rtype == 'grouped_light') {

             return service

        }
    }

    throw 'no grouped_light service found in ' + JSON.stringify(group)

}

let findGroupedLight = id => {

    for (let key in bridge.grouped_light) {

        let grouped_light = bridge.grouped_light[key]

        if (grouped_light.id == id) {

            return grouped_light

        }
    }

    throw 'no grouped light found with id ' + id

}

