let zones = {}

msg.payload.forEach(zone => {
    zones[zone.id] = zone
})

global.set("zones", zones)
return {payload: zones}

let rooms = {}

msg.payload.forEach(room => {
    rooms[room.id] = room
})

global.set("rooms", rooms)
return {payload: rooms}

let scenes = {}
let rooms = global.get("rooms") || {}
let zones = global.get("zones") || {}

let findGroup = (id, groups) => {

    for (let rid in groups) {

        let group = groups[rid]

        if (group.id == id) {
            return group
        }
    }

    return undefined
}

msg.payload.forEach(scene => {

    scenes[scene.id] = scene

    let group = findGroup(scene.group.rid, zones)

    if (group === undefined) {
        group = findGroup(scene.group.rid, rooms)
    }

    if (group !== undefined) {
        scene.group.name = group.metadata.name
    }

    let services = group.grouped_services

    for (let index = 0; index < services.length; ++index) {

        let service = services[index]

        if (service.rtype == "grouped_light") {
            scene.group.grouped_light = service.rid
        }
    }
})

global.set("scenes", scenes)
return {payload: scenes}

let response = []

for (let id in msg.payload) {
    response.push(msg.payload[id])
}
return {payload: response};

