let delay = env.get('DELAY') || 1000
let interval = env.get('INTERVAL') || 0
let topic = env.get('TOPIC') || 'timer'
let payload = env.get('PAYLOAD')

let periodic = () => {

    let p = payload || Date.now()

    node.status({text: p, fill: "green", shape: "dot"})
    node.send({topic: topic, payload: p})

}

context.set('periodic', periodic)

setTimeout(() => {

        periodic()

        if (interval > 0) {

            setInterval(periodic, interval)

        }
    },

    delay)

