let pad = (n) => {

    return ('00' + n).slice(-2)


}

let options = () => {

    let opts = []

    for (let minutes = 1260; minutes <= 1380; minutes += 30) {

        let label = pad(Math.floor(minutes / 60)) + ':' + pad(minutes % 60)
        let opt = {}

        opt[label] = minutes
        opts.push(opt)

    }

    return {
        options: opts
    }
}

setTimeout(() => {

   node.send(options())

}, 500)

