let merge = (currentValue, newValue) => {

    for (let key in newValue) {

        currentValue[key] = newValue[key]

    }

    return currentValue

}

let updateValue = (path, state) => {

    let key = path.shift()    
    let current = state[key] || {}
    let updated = null

    if (path.length == 0) {

        updated = merge(current, msg.payload)
        msg.payload = updated

    } else {

        updated = updateValue(path, current)

    }

    state[key] = updated
    return state

}

updateValue(path, state)
context.set('state', state)
return msg

