let cpu = state[host] || {}

if (msg.hasOwnProperty('error')) {

    if (cpu.hasOwnProperty(attribute)) {

        delete cpu[attribute]

    }

    if (state.hasOwnProperty(host) && Object.keys(cpu).length == 0) {

        delete state[host]

    }

} else {

    cpu[attribute] = msg.payload
    state[host] = cpu

}

context.set('state', state)

