node.status({
    text: msg.topic + ':' + msg.payload,
    shape: 'dot',
    fill: 'blue'
})

////////////////////////////////////////////////////////////////////////////////
//
// Cache the cumulative state of a monitored attribute (CPU temperature, voltage
// etc.) for all hosts
//
////////////////////////////////////////////////////////////////////////////////

let topic = msg.topic.split('/')
let host = topic[1]
let attribute = topic[2]
let state = context.get('state') || {}
let entry = state[host] || {}

if (msg.payload === undefined || msg.payload == null || msg.payload == '' || Object.prototype.hasOwnProperty.call(msg, 'error')) {

    // handle caught errors by deleting the corresponding
    // attribute
    if (Object.prototype.hasOwnProperty.call(entry, attribute)) {

        delete entry[attribute]

    }

    // delete the whole entry if it has no other attributes
    if (Object.prototype.hasOwnProperty.call(state, host) && Object.keys(entry).length == 0) {

        delete state[host]

    }

} else {

    // update the attribuate corresponding
    // to the received event
    entry[attribute] = msg.payload
    state[host] = entry

}

// persist the updated state
context.set('state', state)

////////////////////////////////////////////////////////////////////////////////
//
// Output an HTML <table> containing the values of the given attribute for all
// hosts for which values have been received
//
////////////////////////////////////////////////////////////////////////////////

let unit = env.get('UNIT')
let header = env.get('HEADER')
let table = '<table>'
let first = true

for (let host in state) {

    let entry = state[host]

    if (header && first) {

        table += '<tr><th>host</th>'

        for (let attribute in entry) {

            table += '<th>'
            table += attribute
            table += '</th>'

        }

        table += '</tr>'

    }

    table += '<tr><td>'
    table += host
    table += '</td>'

    for (let attribute in entry) {

        table += '<td>'
        table += entry[attribute] + unit
        table += '</td>'

    }

    table  += '</tr>'
    first = false

}

table += '</table>'
msg.payload = table
return msg

node.status({
    text: msg.topic + ' ' + msg.payload.type,
    shape: 'dot',
    fill: 'blue'
})

const state = flow.get('state') || {}
const type = state[msg.payload.type] || {}
const resource = type[msg.payload.id] || {}

for (let attribute in msg.payload) {

    resource[attribute] = msg.payload[attribute]

}

type[resource.id] = resource
state[resource.type] = type
flow.set('state', state)

msg.topic = env.get('TOPIC') + '/' + resource.type + '/' + resource.id
msg.qos = 2
msg.retain = true
msg.payload = resource

return msg

node.status({
    text: msg.topic + ' ' + msg.payload.type,
    shape: 'dot',
    fill: 'blue'
})

const state = flow.get('state') || {}

if (Object.prototype.hasOwnProperty.call(state, msg.payload.type)) {

    const type = state[msg.payload.type]

    if (Object.prototype.hasOwnProperty.call(type, msg.payload.id)) {

        delete type[msg.payload.id]

    }

}

flow.set('state', state)

msg.topic = env.get('TOPIC') + '/' + msg.payload.type + '/' + msg.payload.id
msg.qos = 2
msg.retain = true
msg.payload = ''

return msg

if (msg.payload.errors.length > 0) {

    throw msg.payload.errors

}

let resources = msg.payload.data

let arrays = {}

resources.forEach((/** @type {{ type: string | number; }} */ resource) => {

    let array = arrays[resource.type] || []

    array.push(resource)
    arrays[resource.type] = array

})

msg.payload = arrays

return msg

let count = context.get('count') || 0

count += 1
context.set('count', count)

node.status({
    fill: "blue",
    shape: "dot",
    text: count
})

return [
    msg,
    { payload: count }
]

/**
 * Determine a `time` based on given values for `sunrise`, `sunset`, `bedtime` and `now`,
 * where `offset`, below, is ((`sunset` - `sunrise`) / 3):
 * 
 * | `time`      | description                                                            |
 * |-------------|------------------------------------------------------------------------|
 * | `morning`   | `now` is between `sunrise` and (`sunrise` + `offset`)                  |
 * | `midday`    | `now` is between (`sunrise` + `offset`) and (`sunset` - `offset`)      |
 * | `afternoon` | `now` is between (`sunset` - `offset`) and `sunset`                    |
 * | `evening`   | `now` is between `sunset` and `bedtime`                                |
 * | `night`     | otherwise [i.e. `now` is between `bedtime` and `sunrise` the next day] |
 */

const theme = msg.payload.daily.theme
const now = new Date(msg.payload.now)
const minutes = now.getHours() * 60 + now.getMinutes()
const sunrise = msg.payload.daily.sunrise
const sunset = msg.payload.daily.sunset
const bedtime = msg.payload.daily.bedtime
const offset = Math.floor((sunset - sunrise) / 3)
let midday = sunrise + offset
let afternoon = sunset - offset

msg.payload = {
    theme: theme,
}

if (minutes < sunrise) {

    msg.payload.time = 'night'

} else if (minutes < midday) {

    msg.payload.time = 'morning'

} else if (minutes < afternoon) {

    msg.payload.time = 'midday'

} else if (minutes < sunset) {

    msg.payload.time = 'afternoon'

} else if (minutes < bedtime) {

    msg.payload.time = 'evening'

} else {

    msg.payload.time = 'night'

}

node.status({
    text: msg.payload.time,
    shape: 'dot',
    fill: 'blue'
})

msg.topic = 'nodered/automation/time'
msg.qos = 2
msg.retain = true
return msg

/*global suncalc*/

/**
 * Send a message when the flow starts, and every midnight
 * thereafter, containing:
 * 
 * - `sunrise`: the number of minutes after midnight at which
 *              the sun will rise
 * 
 * - `sunset`:  the number of minutes after midnight at which
 *              the sun will set
 * 
 * - `bedtime`: a randomly generated number of minutes after
 *              midnight, the later of:
 * 
 *              1. 10:00 PM (22:00) plus or minus 30 minutes
 * 
 *              2. 30 - 60 minutes after sunset, if the preceding
 *                 would occur after the sun has set
 * 
 * - `theme`:   one of `standard`, `tribal`, `spooky` or `jolly`
 *              depending on the day and date
 * 
 *              | theme      | description         |
 *              |------------|---------------------|
 *              | `tribal`   | July 1 - 4          |
 *              | `spooky`   | any day in October  |
 *              | `jolly`    | any day in December |
 *              | `standard` | any other day       |
 * 
 * > Notes:
 * >
 * > - This node depends on the _suncalc_ package to determine
 * >   `sunrise` and `sunset`.
 * >
 * > - It assumes that the geographic coordinates to use with
 * >   the _suncalc_ library are set using `LATITUDE` and
 * >   `LONGITUDE` environment variables.
 * >
 * > - It also assumes that the host OS is configured such that
 * >   functions like `Date.toHours()` will operate in the correct
 * >   time zone for the location specified by `${LATITUDE}`
 * >   and `${LONGITUDE}`
 */

//////////////////////////////////////////////////////////////////////
// sunrise / sunset
//////////////////////////////////////////////////////////////////////

const date = new Date(msg.payload)
const latitude = env.get('LATITUDE')
const longitude = env.get('LONGITUDE')
const times = suncalc.getTimes(date, latitude, longitude)

/**
* @param {Date} date
*/
function toMinutes(date) {

    return date.getHours() * 60 + date.getMinutes()

}

msg.payload = {
    sunrise: toMinutes(times.sunrise),
    sunset: toMinutes(times.sunset),
}

//////////////////////////////////////////////////////////////////////
// theme
//////////////////////////////////////////////////////////////////////

const month = date.getMonth() + 1
const day = date.getDate()

if (month == 7 && day <= 4) {

    msg.payload.theme = 'tribal'

} else if (month == 10) {

    msg.payload.theme = 'spooky'

} else if (month == 12) {

    msg.payload.theme = 'jolly'

} else {

    msg.payload.theme = 'standard'

}

//////////////////////////////////////////////////////////////////////
// bedtime
//////////////////////////////////////////////////////////////////////

msg.payload.bedtime = 1350 - Math.floor(Math.random() * 60)

if (msg.payload.bedtime <= msg.payload.sunset) {

    msg.payload.bedtime = msg.payload.sunset + 30 + Math.floor(Math.random() * 30)

}

//////////////////////////////////////////////////////////////////////
// status and topic
//////////////////////////////////////////////////////////////////////

/**
* @param {number} minutes
*/
function formatMinutes(minutes) {

    const h = Math.floor(minutes / 60)
    const m = minutes % 60
    const a = ((h > 12) ? ' PM' : ' AM')

    return String(h % 12).padStart(2, ' ') + ':' + String(m).padStart(2, '0') + a

}

const formattedSunrise = formatMinutes(msg.payload.sunrise)
const formattedSunset = formatMinutes(msg.payload.sunset)
const formattedBedtime = formatMinutes(msg.payload.bedtime)

node.status({
    fill: 'blue',
    shape: 'dot',
    text: msg.payload.theme
})

msg.topic = 'daily'
return [
    msg,
    { payload: formattedSunrise },
    { payload: formattedSunset },
    { payload: formattedBedtime }
]

let findRoomName = (/** @type {string} */ id) => {

    for (let index = 0; index < msg.rooms.length; ++index) {

        let room = msg.rooms[index]

        if (room.id == id) {

            return room.name

        }
    }

    node.warn('no room found with id ' + id)
    return null
}

let metadata = msg.payload.map((/** @type {{ roomId: any; sceneName: string; sceneId: any; }} */ scene) => {

    let roomName = findRoomName(scene.roomId)

    node.status({
        text: roomName + ' | ' + scene.sceneName,
        shape: 'dot',
        fill: 'blue'
    })

    return {
        sceneId: scene.sceneId,
        sceneName: scene.sceneName,
        roomId: scene.roomId,
        roomName: roomName
    }
})

msg.payload = metadata
return msg
