const selected = []
const lights = msg.payload.light
const pattern = env.get('REGEXP')
const re = new RegExp(pattern)

node.status({
    text: pattern,
    fill: 'green',
    shape: 'ring'
})

lights.forEach(light => {

    if (re.test(light.metadata.name)) {

        selected.push(light)
        node.status({
            text: pattern + ' | ' + selected.length,
            fill: 'green',
            shape: 'dot'
        })

    }
})

msg.payload = selected
return msg

