if (msg.payload.errors.length > 0) {

    throw msg.payload.errors

}

let resources = msg.payload.data

let arrays = {}

resources.forEach(resource => {

    let array = arrays[resource.type] || []

    array.push(resource)
    arrays[resource.type] = array

})

msg.payload = arrays
return msg

let findGroupedLightService = group => {

    for (let index = 0; index < group.services.length; ++index) {

        let service = group.services[index]

        if (service.rtype == 'grouped_light') {

             return service

        }
    }

    throw 'no grouped_light service found in ' + JSON.stringify(group)

}

let findGroupedLight = id => {

    for (let key in msg.metadata.grouped_light) {

        let grouped_light = msg.metadata.grouped_light[key]

        if (grouped_light.id == id) {

            return grouped_light

        }
    }

    throw 'no grouped light found with id ' + id

}

try {

    let service = findGroupedLightService(msg.payload)
    let groupedLight = findGroupedLight(service.rid)

    node.status({
        text: groupedLight.id,
        fill: 'green',
        shape: 'dot'
    })

    msg.topic = 'grouped_light'
    msg.payload = groupedLight
    return msg

} catch (e) {

    node.warn(e)
    return null

}

let group = msg.payload

let scenes = []

msg.metadata.scene.forEach(scene => {

    if (scene.group.rid == group.id) {

        scenes.push(scene)

    }
})

node.status({
    text: 'added ' + scenes.length + ' scenes for group ' + group.metadata.name,
    fill: 'green',
    shape: 'dot'
})

msg.topic = 'scene'
msg.payload = scenes
return msg

node.status({
