setTimeout(() => {

    let pad = (n) => {

        return ('00' + n).slice(-2)

    }

    let options = []

    for (let minutes = 1260; minutes <= 1380; minutes += 30) {

        let label = pad(Math.floor(minutes / 60)) + ':' + pad(minutes % 60)
        let option = {}

        option[label] = minutes
        options.push(option)

    }

    node.send({
        options: options
    })

}, 500)

