let state = context.get('state') || {}
let path = msg.topic.split('/')

node.status({
    text: msg.payload.id,
    shape: 'dot',
    fill: 'blue'
})

let merge = (currentValue, newValue) => {

    for (let key in newValue) {

        currentValue[key] = newValue[key]

    }

    return currentValue

}

let updateValue = (path, state) => {

    let key = path.shift()    
    let current = state[key] || {}
    let updated = null

    if (path.length == 0) {

        updated = merge(current, msg.payload)
        msg.payload = updated

    } else {

        updated = updateValue(path, current)

    }

    state[key] = updated
    return state

}

updateValue(path, state)
context.set('state', state)
return msg

let topic = 'hue/' +
    env.get('BRIDGE_NAME') +
    '/grouped_light/' +
    env.get('GROUPED_LIGHT_ID')

if (msg.status.text == 'node-red:common.status.connected') {

    node.status({
        text: topic,
        shape: "dot",
        fill: "blue"
    })

    return {
        action: "subscribe",
        topic: {
            topic: topic,
            qos: 2
        }
    }

} else {

    node.status(msg.status)

    return {
        action: "subscribe",
        topic: {
            topic: topic,
            qos: 2
        }
    }
}

let findRoomName = id => {

    for (let index = 0; index < msg.rooms.length; ++index) {

        let room = msg.rooms[index]

        if (room.id == id) {

            return room.name

        }
    }

    node.warn('no room found with id ' + id)
    return null
}

let metadata = msg.payload.map(scene => {

    let roomName = findRoomName(scene.roomId)

    node.status({
        text: roomName + ' | ' + scene.sceneName,
        shape: 'dot',
        fill: 'blue'
    })

    return {
        sceneId: scene.sceneId,
        sceneName: scene.sceneName,
        roomId: scene.roomId,
        roomName: roomName
    }
})

msg.payload = metadata
return msg
