function findReferences(groupId, key, resources) {

    const references = []

    resources.forEach(resource => {

        if (resource[key].rid == groupId) {

            references.push(resource)

        }
    })

    return references

}

function getGroup(group, metadata, groups) {

    const groupName = group.metadata.name
    const groupType = group.type
    const groupId = group.id
    const scenes = findReferences(groupId, 'group', metadata.scene)
    const grouped_lights = findReferences(groupId, 'owner', metadata.grouped_light)

    groups[groupName] = {
        id: groupId,
        type: groupType,
        name: groupName,
        grouped_light: grouped_lights[0].id,
        scene: scenes.map(scene => {
            return {
                id: scene.id,
                name: scene.metadata.name
            }
        })
    }

}

const resources = msg.payload.data
const metadata = {}

resources.forEach(resource => {

    const type = metadata[resource.type] || []
    type.push(resource)
    metadata[resource.type] = type

})

const groups = {}
const rooms = metadata.room || []
const zones = metadata.zone || []

rooms.forEach(group => {
    getGroup(group, metadata, groups)
})

zones.forEach(group => {
    getGroup(group, metadata, groups)
})

msg.metadata = metadata
msg.payload = groups

return msg

