/*global suncalc*/

const date = new Date()
const latitude = env.get('LATITUDE')
const longitude = env.get('LONGITUDE')
const times = suncalc.getTimes(date, latitude, longitude)
const start = times.sunrise.getTime()
const end = times.sunset.getTime()

const payload = {
    sunrise: start,
    sunset: end,
    zenith: {}
}

for (let minute = start; minute < end; minute += 60000) {

    const d = new Date(minute)
    const current = payload.zenith.altitude || -100
    const position = suncalc.getPosition(d, latitude, longitude)

    if (position.altitude > current) {

        payload.zenith = {
            altitude: position.altitude,
            azimuth: position.azimuth,
            time: minute
        }
    }
}

const offset = 7200000
payload.midday = payload.zenith.time - offset
payload.afternoon = payload.zenith.time + offset

////////////////////////////////////////////////////////////////
// hack: combine generation of random bedtime with suncalc
// just for convenience of re-using a single set of inject nodes
date.setHours(22, 0, 0, 0)

const bedtime = 1800000 - Math.floor(Math.random() * 3600000)

payload.bedtime = bedtime + date.getTime()
if (payload.bedtime < payload.sunset) {

    payload.bedtime = payload.sunset + 1800000

}
////////////////////////////////////////////////////////////////

////////////////////////////////////////////////////////////////
// hack: combine calculation of theme with suncalc
// just for convenience of re-using a single set of inject nodes
const month = date.getMonth() + 1
const day = date.getDate()

if (month == 7 && day <= 4) {

    payload.theme = 'tribal'

} else if (month == 10) {

    payload.theme = 'spooky'

} else if (month == 12) {

    payload.theme = 'jolly'

} else {

    payload.theme = 'standard'

}
////////////////////////////////////////////////////////////////

msg.payload = payload
msg.topic = 'zenith'

node.status({
    text: new Date(msg.payload.bedtime).toLocaleTimeString(),
    shape: 'dot',
    fill: 'blue'
})

return msg

/*global suncalc*/

const date = new Date(msg.payload.now)
const latitude = env.get('LATITUDE')
const longitude = env.get('LONGITUDE')
const position = suncalc.getPosition(date, latitude, longitude)

const payload = {
    now: msg.payload.now,
    altitude: position.altitude,
    azimuth: position.azimuth
}

for (let key in msg.payload.zenith) {

    payload[key] = msg.payload.zenith[key]

}

if (payload.now > payload.sunrise && payload.now < payload.bedtime) {

    if (payload.now < payload.midday) {

        payload.time = 'morning'

    } else if (payload.now < payload.afternoon) {

        payload.time = 'midday'

    } else if (payload.now < payload.sunset) {

        payload.time = 'afternoon'

    } else {

        payload.time = 'evening'

    }

} else {

    payload.time = 'night'

}

node.status({
    text: payload.time,
    shape: 'dot',
    fill: 'blue'
})

/**
* @param {string} topic
* @param {{ [x: string]: any; now?: any; altitude?: any; azimuth?: any; }} payload
* @param {{ topic: string; payload: any; qos: number; retain: boolean; }[]} msgs
*/
function splitPayload(topic, payload, msgs) {

    for (let key in payload) {

        const value = payload[key]

        if ((typeof value) == 'object') {

            msgs = msgs.concat(splitPayload(topic + key + '/', value, []))

        } else {

            msgs.push({
                topic: topic + key,
                payload: value
            })
        }
    }

    return msgs

}

const msgs = splitPayload('', payload, [])

return [msgs]

let findRoomName = (/** @type {string} */ id) => {

    for (let index = 0; index < msg.rooms.length; ++index) {

        let room = msg.rooms[index]

        if (room.id == id) {

            return room.name

        }
    }

    node.warn('no room found with id ' + id)
    return null
}

let metadata = msg.payload.map((/** @type {{ roomId: any; sceneName: string; sceneId: any; }} */ scene) => {

    let roomName = findRoomName(scene.roomId)

    node.status({
        text: roomName + ' | ' + scene.sceneName,
        shape: 'dot',
        fill: 'blue'
    })

    return {
        sceneId: scene.sceneId,
        sceneName: scene.sceneName,
        roomId: scene.roomId,
        roomName: roomName
    }
})

flow.set('metadata', metadata)

msg.payload = metadata
return msg
